import React, { useRef, useState } from 'react';
import AvatarEditor from 'react-avatar-editor';

const ProfileImageSelector = () => {
  const editorRef = useRef();
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    setSelectedImage(URL.createObjectURL(file));
  };

  const handleCropImage = () => {
    if (editorRef.current) {
      const canvas = editorRef.current.getImageScaledToCanvas();
      const croppedImage = canvas.toDataURL(); // You can use this cropped image data URL as needed
      // Perform further actions with the cropped image, such as uploading to a server
    }
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        onChange={handleImageChange}
      />
      {selectedImage && (
        <div style={{ borderRadius: '50%', overflow: 'hidden', width: '250px', height: '250px' }}>
          <AvatarEditor
            ref={editorRef}
            image={selectedImage}
            width={250}
            height={250}
            border={0} // Set border to 0 to remove the default border
            borderRadius={250} // Set borderRadius to half of the width/height to make it circular
            color={[255, 255, 255, 0.6]} // Customize the background color of the editor
            scale={1.2} // Adjust the scale factor for zooming the image within the editor
            rotate={0} // Set the initial rotation angle of the image in degrees
          />
        </div>
      )}
      <button onClick={handleCropImage}>Crop Image</button>
    </div>
  );
};

export default ProfileImageSelector;
