import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

export default function Publicacion({
  user,
  profileimg,
  date,
  text,
  category,
  image,
  comments,
}) {

  const [commentText, setCommentText] = useState('');
  const inpBackCol = { backgroundColor: '#2b3036', borderColor: '#86857e', outlineColor: 'none', width: '100%', display: 'flex' }
  const [data, setData] = useState([]);

  const fetchData = async () => {
    try {
      const responsePromises = comments.map(comment =>
        axios.get("http://localhost:3000/comments/" + comment)
      );

      const responses = await Promise.all(responsePromises);
      const responseData = responses.map(response => response.data);
      console.log(responseData);
      setData(responseData);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    const getData = async () => {
      await fetchData();
    };
    getData();
  }, []);

  const fetchDataUsu = async (usuarioId) => {
    try {
      const response = await axios.get(`https://example.com/api/users/${usuarioId}`);
      const userData = response.data;
      return userData; // Return the user data
    } catch (error) {
      console.error(error);
      return null; // Return null or handle the error case
    }
  };

  const handleComment = async (event) => {
  }

  function renderPublications() {
    if (data && data.length > 0) {
      return data.map((item, index) => {

        if (item.isNovedad) {
          return (
            <Publicacion
              key={index}
              profileimg={item.user.image}
              user={item.user.name}
              date={item.date}
              text={item.content}
              category={item.tittle}
              image={item.image}
              comments={item.comments}
            ></Publicacion>
          );
        }
        return null; // If item.isVerified is false, return null to skip rendering the component
      });
    }}

  return (
    <div className="container mb-4">
      <div className="card bg-dark text-white">
        <div className="card-header">
          <div className="row">
            <div className="col-6">
              <img
                src={profileimg}
                className="img-fluid rounded"
                alt="👤"
                style={{ height: '20px', width: '20px' }}
              />
              <strong>{" " + user + " / " + category}</strong>
            </div>
            <div className="col-6 text-end text-muted">{date}</div>
          </div>
        </div>
        <div className="card-body ">
          <div className="row" style={{ display: "flex" }}>
            <div className="col-md-2 col-lg-2">
              <img
                src={image}
                className="img-fluid rounded"
                alt="Error al cargar la imagen 😢"
                style={{ height: "100%", objectFit: "cover" }}
              />
            </div>
            <div className="col-md-10 col-lg-10">
              <p className="card-text">{text}</p>
            </div>
          </div>
        </div>
        <div className="card-footer">
          <h6>Comentarios:</h6>
          {/* {data.map(async (data, index) => (
            <p key={index}>{"💬" + await fetchDataUsu(data.user) + ": " + data.content}</p>
          ))}  */}
          {renderComentarios()}
          <div className="row">
            <div className="col-12"> <h6> <textarea type="text" className="text-white" style={inpBackCol} value={commentText} onChange={(event) => setCommentText(event.target.value)} placeholder="Espacio para que comentes ¡expresate!" /></h6></div>
          </div>
          <div className="row">
            <div className="text-end">  <button type="button" className="btn align-text-end text-white" style={{ backgroundColor: '#11141b', alignSelf: 'end' }} onClick={handleComment} >comentar!</button></div>
          </div>
        </div>
      </div>
    </div>
  );
}
