import React, { useRef, useState } from 'react';
import AvatarEditor from 'react-avatar-editor';

const ProfileImageSelector = () => {
  const editorRef = useRef();
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [showCrop, setShowCrop] = useState(false);

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    setSelectedImage(URL.createObjectURL(file));
    setPreviewImage(URL.createObjectURL(file));
    setShowCrop(true);
  };

  const handleCropImage = () => {
    if (editorRef.current) {
      const canvas = editorRef.current.getImageScaledToCanvas();
      const croppedImage = canvas.toDataURL(); // You can use this cropped image data URL as needed
      // Perform further actions with the cropped image, such as uploading to a server
    }
    setShowCrop(false);
  };

  const handleCancelCrop = () => {
    setShowCrop(false);
  };

  return (
    <div>
      {!showCrop && (
        <div>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
          {previewImage && (
            <img src={previewImage} alt="Selected Profile Image" style={{ width: '250px', height: '250px' }} />
          )}
        </div>
      )}

      {showCrop && (
        <div>
          <AvatarEditor
            ref={editorRef}
            image={selectedImage}
            width={250}
            height={250}
            border={0}
            borderRadius={250}
            color={[255, 255, 255, 0.6]}
            scale={1.2}
            rotate={0}
          />
          <button onClick={handleCropImage}>Confirm Crop</button>
          <button onClick={handleCancelCrop}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default ProfileImageSelector;
