        <div className="card-body ">
          <div className="row" style={{ display: "flex" }}>
            <div className="col-md-2 col-lg-2">
              <img
                src={image}
                className="img-fluid rounded"
                alt="Error al cargar la imagen ðŸ˜¢"
                style={{ height: "100%", objectFit: "cover" }}
              />
            </div>
            <div className="col-md-10 col-lg-10">
              <p className="card-text">{text}</p>
            </div>
          </div>
        </div>
        <div className="card-footer">
          <h6>Comentarios:</h6>
          {comments.map((comment, index) => (
            <p key={index}>{"ðŸ’¬" + comment.user.name + ": " + comment.content}</p>
          ))}
          <h6> <input type="text" className="text-white" style={inpBackCol} value={category} onChange={(event) => setCategory(event.target.value)} placeholder="Categoria" /></h6>
        </div>
