import React, { useRef, useState } from 'react';
import AvatarEditor from 'react-avatar-editor';

const ProfileImageSelector = () => {
  const editorRef = useRef();
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [croppedImage, setCroppedImage] = useState(null);
  const [showCrop, setShowCrop] = useState(false);

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    setSelectedImage(URL.createObjectURL(file));
    setPreviewImage(URL.createObjectURL(file));
    setShowCrop(true);
  };

  const handleCropImage = () => {
    if (editorRef.current) {
      const canvas = editorRef.current.getImageScaledToCanvas();
      const croppedImage = canvas.toDataURL(); // You can use this cropped image data URL as needed
      setCroppedImage(croppedImage);
    }
    setShowCrop(false);
  };

  const handleCancelCrop = () => {
    setShowCrop(false);
  };

  const applyCircularMask = (imageURL) => {
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    const image = new Image();

    image.src = imageURL;
    canvas.width = image.width;
    canvas.height = image.height;

    context.drawImage(image, 0, 0);

    context.beginPath();
    context.arc(
      canvas.width / 2,
      canvas.height / 2,
      canvas.width / 2,
      0,
      2 * Math.PI
    );
    context.closePath();
    context.clip();

    const circularImageURL = canvas.toDataURL();
    return circularImageURL;
  };

  return (
    <div>
      {!showCrop && (
        <div>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
          {previewImage && (
            <img src={previewImage} alt="Selected Profile Image" style={{ width: '250px', height: '250px' }} />
          )}
        </div>
      )}

      {showCrop && (
        <div>
          <AvatarEditor
            ref={editorRef}
            image={selectedImage}
            width={250}
            height={250}
            border={0}
            borderRadius={250}
            color={[255, 255, 255, 0.6]}
            scale={1.2}
            rotate={0}
          />
          <button onClick={handleCropImage}>Confirm Crop</button>
          <button onClick={handleCancelCrop}>Cancel</button>
        </div>
      )}

      {croppedImage && (
        <div>
          <h2>Cropped Image:</h2>
          <img
            src={applyCircularMask(croppedImage)}
            alt="Cropped Profile Image"
            style={{ width: '250px', height: '250px', borderRadius: '50%' }}
          />
        </div>
      )}
    </div>
  );
};

export default ProfileImageSelector;
