import React from "react";
import Publicacion from "./Publicacion";
import { useState, useEffect } from "react";
import axios from "axios";

export default function Novedades() {
  const [data, setData] = useState();

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:3000/publications");
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    const getData = async () => {
      await fetchData();
    };

    getData();
  }, []);

  function renderPublications() {
    if (data && data.length > 0) {
      //return data.map((item) => <h2 key={item._id}>{item._id}</h2>);
      return data.map((item, index) => <Publicacion key={index} user={item.user.name}
      date={item.date}
      text={item.content}
      category={item.tittle}
      image={item.image}
      comments={item.comments}>{item}</Publicacion>);
    } else {
      return <p>Loading...</p>;
    }
  }


  const publicationDataArray = [
    {
      user: "Robert Kirkman",
      date: "3:03 AM, May 18-2023",
      text: "va a salir nueva temporada!",
      category: "Anuncio",
      image:
        "https://m.media-amazon.com/images/I/61YLVtIOhZL._RI_SX720_FMjpg_.jpg",
      comments: ["游때游때游때", "Que bendici칩n."],
    },
  ];

  return (
    <div>
      <h1
        className="text-center mt-3 display-1"
        style={{ fontFamily: "'Bangers', cursive", color: "#fee566" }}
      >
        {" "}
        Novedades y Noticias{" "}
      </h1>
      <hr />

      {renderPublications()}


      {/*  <Publicacion data={data[0]}></Publicacion> */}

      {/* {publicationDataArray.map((publicationData, index) => (
      <Publicacion key={index} {...publicationData} />
    ))} */}

      {/* {data.map((publicationData, index) => (
        <Publicacion key={index} {...publicationData} />
      ))} */}
    </div>
  );
}
