import React, { useState } from 'react';
import AvatarEditor from 'react-avatar-editor';
import vacio from '../styles/imgs/empty.jpg';

export default function ProfileImageSelect() {
    const [profileImage, setProfileImage] = useState(vacio);
    const [selectedImage, setSelectedImage] = useState(null);
    const [editor, setEditor] = useState(null);
    const [confirmed, setConfirmed] = useState(false);
    const [resultImage, setResultImage] = useState(null);

    const handleImageSelect = (event) => {
        const file = event.target.files[0];
        setSelectedImage(URL.createObjectURL(file));
        setConfirmed(false);
        setResultImage(null);
    };

    const handleImageUpload = () => {
        if (editor) {
            const canvas = editor.getImageScaledToCanvas();
            const croppedImage = canvas.toDataURL();
            setResultImage(croppedImage);
            setProfileImage(croppedImage);
            setConfirmed(true);
        }
    };


    return (
        <div className='container'>

            <div className='row justify-content-center mb-1 p-5'>
                <div className='col-4 text-center'>
                    {!confirmed && (
                    <img src={profileImage} alt="Error :/" className="img-fluid rounded border border-dark" ></img>
                    )
                    }
                    {confirmed && (
                    <>
                        <input type="file" onChange={handleImageSelect} accept="image/*" />
                        {selectedImage && (
                            <AvatarEditor
                                ref={(ref) => setEditor(ref)}
                                image={selectedImage}
                                width={250}
                                height={250}
                                color={[0, 0, 0, 0.4]}
                                scale={1}
                                crossOrigin="anonymous"
                                style={{ width: '20%', height: 'auto', aspectRatio: 1 / 1 }}
                            />
                        )}
                        {selectedImage && <button onClick={handleImageUpload}>Confirm</button>}
                    </>
                )}
                </div>
                <button type="button" className="btn col-4" style={{ backgroundColor: '#fee566' }}>Cambiar foto</button>
               
            </div>


            <div style={{ margin: 0 }}>
                {!confirmed && (
                    <>
                        <input type="file" onChange={handleImageSelect} accept="image/*" />
                        {selectedImage && (
                            <AvatarEditor
                                ref={(ref) => setEditor(ref)}
                                image={selectedImage}
                                width={250}
                                height={250}
                                color={[0, 0, 0, 0.4]}
                                scale={1}
                                crossOrigin="anonymous"
                                style={{ width: '20%', height: 'auto', aspectRatio: 1 / 1 }}
                            />
                        )}
                        {selectedImage && <button onClick={handleImageUpload}>Confirm</button>}
                    </>
                )}
            </div>
        </div>
    )
}
