import React, { useState } from 'react';
import AvatarEditor from 'react-avatar-editor';

const ProfilePhotoSelection = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [editor, setEditor] = useState(null);

  const handleImageSelect = (event) => {
    const file = event.target.files[0];
    setSelectedImage(URL.createObjectURL(file));
  };

  const handleImageUpload = () => {
    if (editor) {
      const canvas = editor.getImageScaledToCanvas().toDataURL();
      // Perform the upload logic with the canvas data
      // Example: send the canvas data to your backend API
      console.log(canvas);
    }
  };

  return (
    <div>
      <input type="file" onChange={handleImageSelect} accept="image/*" />
      {selectedImage && (
        <AvatarEditor
          ref={(ref) => setEditor(ref)}
          image={selectedImage}
          width={250}
          height={250}
          color={[255, 255, 255, 0.6]} // Optional background color
          scale={1}
          crossOrigin="anonymous"
          // Adjust the aspect ratio as per your desired rectangular shape
          // In this example, it is set to 16:9
          // You can adjust the width and height ratio accordingly
          style={{ width: '20%', height: 'auto', aspectRatio: 16 / 9 }}
        />
      )}
      <button onClick={handleImageUpload}>Confirm</button>
    </div>
  );
};

function App() {
  return (
    <div>
      <h1>Profile Photo Selection</h1>
      <ProfilePhotoSelection />
    </div>
  );
}

export default App;
